<?php

namespace Tests\Unit;

use Tests\TestCase;
use App\Models\City;
use App\Models\Country;
use App\Models\AvailableJob;
use App\Services\ApplicationServices;
use App\Services\PhotoServices;
use Illuminate\Support\Facades\Storage;
use Illuminate\Foundation\Testing\RefreshDatabase;

class PhotoServicesTest extends TestCase
{
    use RefreshDatabase;
    /**
     * A basic unit test example.
     *
     * @return void
     */
    public function test_photo_stored_and_uploaded()
    {
        $availableJob = AvailableJob::factory()->create();
        $country = Country::factory()->create();
        $city = City::factory()->create();
        $fakeRequest = [
            'available_job_id' => $availableJob->id,
            'first_name' => 'first',
            'last_name' => 'last',
            'email' => 'email@email.net',
            'phone' => '081336363259',
            'nationality' => 'indonesia',
            'driving_license' => '081336363259',
            'birth_place' => 'bandung',
            'birth_date' => 1645549200,
            'country_id' => $country->id,
            'city_id' => $city->id,
            'address' => 'my address',
            'postal_code' => 15233,
        ];
        $fakeBase64 = [
            'base_64_photo' => 'data:image/png;base64,'
        ];
        $payload = (new ApplicationServices())->payloadPreparetion($fakeRequest);
        $application = (new ApplicationServices())->storeToDB($payload);
        $photoPath = (new PhotoServices())->upload($application, $fakeBase64);

        $this->assertTrue(Storage::disk('public')->exists(explode('storage/', $photoPath)[1]));
    }

    public function test_delete_photo_in_db_and_storage()
    {
        $availableJob = AvailableJob::factory()->create();
        $country = Country::factory()->create();
        $city = City::factory()->create();
        $fakeRequest = [
            'available_job_id' => $availableJob->id,
            'first_name' => 'first',
            'last_name' => 'last',
            'email' => 'email@email.net',
            'phone' => '081336363259',
            'nationality' => 'indonesia',
            'driving_license' => '081336363259',
            'birth_place' => 'bandung',
            'birth_date' => 1645549200,
            'country_id' => $country->id,
            'city_id' => $city->id,
            'address' => 'my address',
            'postal_code' => 15233,
        ];
        $fakeBase64 = [
            'base_64_photo' => 'data:image/png;base64,'
        ];
        $payload = (new ApplicationServices())->payloadPreparetion($fakeRequest);
        $application = (new ApplicationServices())->storeToDB($payload);
        $photoPath = (new PhotoServices())->upload($application, $fakeBase64);
        $actualPath = explode('storage/', $photoPath)[1];

        $this->assertTrue(Storage::disk('public')->exists($actualPath));

        $deleted = (new PhotoServices())->deleteData($application);

        $this->assertEquals($application->code, $deleted->code);
        $this->assertFalse(Storage::exists($actualPath));
    }
}
